// src/pages/Home.js
import React, { useEffect, useState } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useProjectTypes } from '../hooks/useProjectTypes';

const Home = () => {
  const { projectTypes, loading } = useProjectTypes();
  const defaultProjectTypes = ['Games', 'Websites', 'Mobile Apps', 'Design'];
  
  const displayTypes = loading ? defaultProjectTypes : projectTypes.length > 0 ? projectTypes : defaultProjectTypes;
  
  return (
    <HomeContainer>
      <ProjectTypesSection>
        <TypesContainer>
          {displayTypes.map((type, index) => (
            <TypeCard
              key={type}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
            >
              <Link to={`/projects/${type}`}>
                <TypeTitle>{type}</TypeTitle>
                <TypeDescription>View {type} projects</TypeDescription>
              </Link>
            </TypeCard>
          ))}
        </TypesContainer>
      </ProjectTypesSection>
    </HomeContainer>
  );
};

// Styled Components
const HomeContainer = styled.div`
  padding: 2rem;
  background-color: #0a0e17;
  min-height: 100vh;
  color: white;
`;

const ProjectTypesSection = styled.section`
  padding-top: 2rem;
`;

const TypesContainer = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(250px, 1fr));
  gap: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const TypeCard = styled(motion.div)`
  background-color: rgba(255, 255, 255, 0.05);
  border-radius: 8px;
  overflow: hidden;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  height: 200px;
  display: flex;
  align-items: center;
  justify-content: center;
  
  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.3);
    background-color: rgba(196, 91, 91, 0.2);
  }
  
  a {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: center;
    height: 100%;
    width: 100%;
    padding: 2rem;
    color: white;
    text-decoration: none;
  }
`;

const TypeTitle = styled.h3`
  font-size: 2rem;
  text-align: center;
  margin-bottom: 1rem;
`;

const TypeDescription = styled.p`
  font-size: 1rem;
  opacity: 0.7;
  text-align: center;
`;

export default Home;
