// src/pages/ProjectList.js
import React, { useEffect, useState } from 'react';
import { useParams, Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';
import { useProjects } from '../hooks/useProjects';

const ProjectList = () => {
  const { type } = useParams();
  const { projects, loading } = useProjects(type);
  
  if (loading) {
    return <LoadingIndicator>Loading projects...</LoadingIndicator>;
  }
  
  return (
    <ProjectListContainer>
      <TypeTitle>{type}</TypeTitle>
      
      {projects.length === 0 ? (
        <NoProjects>No projects found for this category.</NoProjects>
      ) : (
        <ProjectsGrid>
          {projects.map((project, index) => (
            <ProjectCard
              key={project.id}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
            >
              <Link to={`/project/${project.id}`}>
                <ProjectImage 
                  src={`/projects/${project.id}/images/main.jpg`} 
                  alt={project.id}
                  onError={(e) => {
                    e.target.src = '/placeholder-image.jpg';
                  }}
                />
                <ProjectInfo>
                  <ProjectTitle>{project.id}</ProjectTitle>
                  <TechList>
                    {project.info.technologies?.slice(0, 3).map((tech, i) => (
                      <TechItem key={i}>{tech}</TechItem>
                    ))}
                  </TechList>
                </ProjectInfo>
              </Link>
            </ProjectCard>
          ))}
        </ProjectsGrid>
      )}
      
      <BackLink to="/">‚Üê Back to all categories</BackLink>
    </ProjectListContainer>
  );
};

// Styled Components
const ProjectListContainer = styled.div`
  padding: 2rem;
  background-color: #0a0e17;
  min-height: 100vh;
  color: white;
`;

const TypeTitle = styled.h1`
  font-size: 3rem;
  margin-bottom: 3rem;
  text-align: center;
`;

const LoadingIndicator = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  height: 200px;
  color: white;
  font-size: 1.2rem;
`;

const NoProjects = styled.p`
  text-align: center;
  font-size: 1.2rem;
  margin-top: 3rem;
`;

const ProjectsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
  gap: 2rem;
  max-width: 1200px;
  margin: 0 auto;
`;

const ProjectCard = styled(motion.div)`
  background-color: rgba(255, 255, 255, 0.05);
  border-radius: 8px;
  overflow: hidden;
  transition: transform 0.3s ease, box-shadow 0.3s ease;
  
  &:hover {
    transform: translateY(-5px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.3);
  }
  
  a {
    color: white;
    text-decoration: none;
  }
`;

const ProjectImage = styled.img`
  width: 100%;
  height: 200px;
  object-fit: cover;
`;

const ProjectInfo = styled.div`
  padding: 1.5rem;
`;

const ProjectTitle = styled.h3`
  font-size: 1.5rem;
  margin-bottom: 0.5rem;
`;

const TechList = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: 0.5rem;
  margin-top: 1rem;
`;

const TechItem = styled.span`
  background-color: rgba(255, 255, 255, 0.1);
  padding: 0.25rem 0.75rem;
  border-radius: 20px;
  font-size: 0.8rem;
`;

const BackLink = styled(Link)`
  display: inline-block;
  margin-top: 3rem;
  color: white;
  text-decoration: none;
  font-size: 1.1rem;
  
  &:hover {
    text-decoration: underline;
  }
`;

export default ProjectList;
