// src/components/Header.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import '../styles/Header.css';

// Array of roles to cycle through
const roles = ["DESIGNER", "DEVELOPER", "CREATOR"];

const Header = () => {
  const [roleIndex, setRoleIndex] = useState(0);
  
  // Effect to cycle through roles every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setRoleIndex((prevIndex) => (prevIndex + 1) % roles.length);
    }, 5000);
    
    return () => clearInterval(interval);
  }, []);

  return (
    <HeaderContainer>
      <Navigation>
        <NavItems>
          <NavItem><Link to="/projects/Games">Games</Link></NavItem>
          <NavItem><Link to="/projects/Architecture">Architecture</Link></NavItem>
          <NavItem><Link to="/projects/Websites">Websites</Link></NavItem>
          <NavItem><Link to="/projects/Svenska">Svenska</Link></NavItem>
        </NavItems>
      </Navigation>
      
      <HeroSection>
        <NameContainer>
          <Name>DAVID RAJALA</Name>
        </NameContainer>
        
        <RoleContainer>
          <AnimatePresence mode="wait">
            <Role
              key={roleIndex}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.5 }}
            >
              {roles[roleIndex]}
            </Role>
          </AnimatePresence>
        </RoleContainer>
        
        <Tagline>Building digital experiences that matter</Tagline>
      </HeroSection>
    </HeaderContainer>
  );
};

// Styled Components
const HeaderContainer = styled.header`
  display: flex;
  flex-direction: column;
  width: 100%;
  min-height: 100vh;
  background-color: #0a0e17;
  color: white;
`;

const Navigation = styled.nav`
  display: flex;
  justify-content: flex-end;
  padding: 2rem;
`;

const NavItems = styled.ul`
  display: flex;
  list-style: none;
  gap: 2rem;
`;

const NavItem = styled.li`
  a {
    color: white;
    text-decoration: none;
    font-size: 1rem;
    transition: color 0.3s ease;
    
    &:hover {
      color: #c45b5b;
    }
  }
`;

const HeroSection = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  flex-grow: 1;
  text-align: center;
`;

const NameContainer = styled.div`
  margin-bottom: 1rem;
`;

const Name = styled.h1`
  font-size: 4rem;
  font-weight: 700;
  letter-spacing: 3px;
`;

const RoleContainer = styled.div`
  height: 5rem;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Role = styled(motion.h2)`
  font-size: 5rem;
  font-weight: 700;
  color: #c45b5b;
  letter-spacing: 2px;
`;

const Tagline = styled.p`
  font-size: 1.2rem;
  margin-top: 2rem;
  opacity: 0.8;
`;

export default Header;
