// src/components/Header.js
import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion, AnimatePresence } from 'framer-motion';
import '../styles/Header.css';

// Array of roles to cycle through
const roles = ["DESIGNER", "DEVELOPER", "CREATOR"];

const Header = () => {
  const [roleIndex, setRoleIndex] = useState(0);
  
  // Effect to cycle through roles every 5 seconds
  useEffect(() => {
    const interval = setInterval(() => {
      setRoleIndex((prevIndex) => (prevIndex + 1) % roles.length);
    }, 5000);
    
    return () => clearInterval(interval);
  }, []);

  return (
    <HeaderContainer>
      <HeroSection>
        <NameContainer>
          <Name>DAVID RAJALA</Name>
        </NameContainer>
        
        <RoleContainer>
          <AnimatePresence mode="wait">
            <Role
              key={roleIndex}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, y: -20 }}
              transition={{ duration: 0.5 }}
            >
              {roles[roleIndex]}
            </Role>
          </AnimatePresence>
        </RoleContainer>
        
        <Tagline>Building digital experiences that matter</Tagline>
        
        {/* Moved up from Home component */}
        <WorkSection>
          <WorkTitle>My Work</WorkTitle>
          <ScrollIndicator>↓ Scroll to explore ↓</ScrollIndicator>
        </WorkSection>
      </HeroSection>
    </HeaderContainer>
  );
};

// Styled Components
const HeaderContainer = styled.header`
  display: flex;
  flex-direction: column;
  width: 100%;
  min-height: 100vh;
  background-color: #0a0e17;
  color: white;
`;

const HeroSection = styled.div`
  display: flex;
  flex-direction: column;
  justify-content: center;
  align-items: center;
  flex-grow: 1;
  text-align: center;
`;

const NameContainer = styled.div`
  margin-bottom: 1rem;
`;

const Name = styled.h1`
  font-size: 4rem;
  font-weight: 700;
  letter-spacing: 3px;
`;

const RoleContainer = styled.div`
  height: 5rem;
  display: flex;
  align-items: center;
  justify-content: center;
`;

const Role = styled(motion.h2)`
  font-size: 5rem;
  font-weight: 700;
  color: #c45b5b;
  letter-spacing: 2px;
`;

const Tagline = styled.p`
  font-size: 1.2rem;
  margin-top: 2rem;
  opacity: 0.8;
`;

const WorkSection = styled.div`
  margin-top: 4rem;
  display: flex;
  flex-direction: column;
  align-items: center;
`;

const WorkTitle = styled.h2`
  font-size: 2rem;
  font-weight: 600;
  margin-bottom: 1rem;
`;

const ScrollIndicator = styled.div`
  margin-top: 1rem;
  font-size: 1rem;
  opacity: 0.7;
  animation: bounce 2s infinite;

  @keyframes bounce {
    0%, 20%, 50%, 80%, 100% {
      transform: translateY(0);
    }
    40% {
      transform: translateY(-10px);
    }
    60% {
      transform: translateY(-5px);
    }
  }
`;

export default Header;
